import React, { useState } from "react";
import "./style.scss";

function Register() {
  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [socialName, setSocialName] = useState();
  const [errors, setErrors] = useState({ name: "", email: "", socialName: "" });

  const validEmailRegex = RegExp(
    /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i
  );
  const validateForm = (errors) => {
    let valid = true;
    Object.values(errors).forEach((val) => val.length > 0 && (valid = false));
    return valid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleChange = (e) => {
    e.preventDefault();
    const { name, value } = e.target;
    let temperrors = errors;

    switch (name) {
      case "name":
        temperrors.name = value.length < 5 ? "Please check your name" : "";
        setName(value);
        break;
      case "email":
        temperrors.email = validEmailRegex.test(value)
          ? ""
          : "Please check your email";
        setEmail(value);
        break;
      case "socialname":
        temperrors.socialname =
          value.length < 5 ? "Please check your social user name" : "";
        setSocialName(value);
        break;
      default:
        break;
    }
    setErrors({ ...temperrors });
    console.log(temperrors);
  };

  return (
    <div className="register">
      <div className="register-tag"></div>
      <div className="register-title">Fashionista Pre-register</div>
      <form
        className="register-form"
        onSubmit={(e) => handleSubmit(e)}
        noValidate
      >
        <input
          name="name"
          placeholder="NAME"
          value={name}
          onChange={(e) => {
            handleChange(e);
          }}
          noValidate
        ></input>
        {errors.name.length > 0 && <span className="error">{errors.name}</span>}
        <input
          name="email"
          type="text"
          placeholder="EMAIL"
          value={email}
          onChange={(e) => {
            handleChange(e);
          }}
          noValidate
        ></input>
        <input
          name="socialname"
          placeholder="SOCIAL MEDIA USERNAME"
          value={socialName}
          noValidate
        ></input>
      </form>
    </div>
  );
}

export default Register;
